import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  Table,
  Space,
  Modal,
  PageHeader,
  Input,
  Tag,
  Image,
  Divider,
  Avatar,
  Button,
  List
} from "antd";
import { Link } from "react-router-dom";
import { EyeOutlined } from "@ant-design/icons";
import { listarPlatillos } from "../../api/platillo";
import "./Platillos.scss";

export default function Platillos() {
  const [reloadPlatillos, setReloadPlatillos] = useState(false);
  const [baseData, setBaseData] = useState([]);
  const [filterTable, setFilterTable] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function listarPlatillosApi() {
      let response = await listarPlatillos();
      console.log(response)
      if (response) {
        //console.log(response);
        let newArr = response.data.map(function (item) {
          return {
            _id: item._id,
            nombre: `${item.nombre}`,
            descripcion: item.descripcion,
            categoria: item.categoria,
            ruta_imagen: item.ruta_imagen,
          };
        });
        setBaseData(newArr);
        setIsLoading(false);
      }
    }
    listarPlatillosApi();
    setReloadPlatillos(false);
  }, [reloadPlatillos]);

  const columns = [
    {
      title: "",
      dataIndex: "ruta_imagen",
      key: "ruta_imagen",
      align: 'center',
      width: "50",
      render: ruta_imagen => 
      <Space size="middle">
        {
          ruta_imagen.length == 0 ?  
          <Avatar
          size={128}
          src="error"
          fallback="data:image/png;base64,"
          />      
          : 
          <Avatar
            size={128}
            src='http://localhost:3977/public/calendario.png'
          />
        }
      </Space>
    },
    {
      title: "Nombre",
      dataIndex: "nombre",
      key: "nombre",
      sorter: (a, b) => a.nombre.localeCompare(b.nombre),
      sortDirections: ["descend", "ascend"],
      render: (text, record) => (<Link to={`/cocina/platillos/${record._id}`}><h1>{text}</h1></Link>)
    },
    {
      title: "DescripciÃ³n",
      dataIndex: "descripcion",
      key: "descripcion",
    },
    {
      title: 'Categoria',
      key: 'categoria',
      dataIndex: 'categoria',
      render: categoria => (
        <>
          {categoria.map(catego => {
            let color = catego.length > 5 ? 'volcano' : 'volcano';
            if (catego === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={catego}>
                {catego.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: "",
      key: "action",
      dataIndex: "nombre",
      render: (nombre, record) => (
        <Space size="middle">
            {" "}
            <Button
                type="dashed"
                icon={<EyeOutlined />}
                href={`/cocina/platillos/${record._id}`}
            >
              Ver recetas
            </Button>
        </Space>
      ),
    },
  ];

  const search = (value) => {
    if (value !== "") {
      const filterTable = baseData.filter((o) =>
        Object.keys(o).some((k) =>
          String(o[k]).toLowerCase().includes(value.toLowerCase())
        )
      );
      setFilterTable(filterTable);
    } else {
      setFilterTable(null);
    }
  };

  return (
    <> 
      <div className="master-container">
        <PageHeader className="site-page-header" title="Platillos">

        <Divider style={{ marginTop: "0px" }} />
        <Row>
          <Col lg={1} />
          <Col lg={22}>
            <Input.Search
              style={{ margin: "0 0 10px 0" }}
              placeholder="Buscar..."
              enterButton
              onSearch={search}
            />
            <List
              loading={isLoading}
              itemLayout="horizontal"
              dataSource={filterTable == null ? baseData : filterTable}
              bordered={false}
              pagination={{onChange: (page) => {},pageSize: 5,}}
              renderItem={(item) => (
                <List.Item
                  actions={[            
                      <Button
                        type="dashed"
                        icon={<EyeOutlined />}
                        href={`/cocina/platillos/${item._id}`}
                    >
                      Ver recetas
                    </Button>]}
                >
                  <List.Item.Meta
                    avatar={
                      item.ruta_imagen.length == 0 ?  
                      <Avatar
                        size={128}
                        src="error"
                        fallback="data:image/png;base64,"
                      />      
                      : 
                      <Avatar
                        size={130}
                        src={item.ruta_imagen}
                      />
                    }
                    title={`${item.nombre ? item.nombre : ''}`}
                    description={item.descripcion ? item.descripcion : ''}
                  />
                  {<>
                      {item.categoria.map(catego => {
                        let color = catego.length > 5 ? 'volcano' : 'volcano';
                        if (catego === 'loser') {
                          color = 'volcano';
                        }
                        return (
                          <Tag color={color} key={catego}>
                            {catego.toUpperCase()}
                          </Tag>
                        );
                      })}
                    </>   
                    }
                </List.Item>
              )}
            />
          </Col>
          <Col lg={1} />
        </Row>
        </PageHeader>
      </div>
    </>
  );
}

